# Read the file
with open('/home/user/webapp/src/index.tsx', 'r', encoding='utf-8') as f:
    content = f.read()

# For each data-ru="X" data-am="Y", replace Y with the correct Armenian translation for X
def replace_am(match):
    ru_text = match.group(1)
    between = match.group(2)
    old_am = match.group(3)
    
    if ru_text in TRANSLATIONS:
        new_am = TRANSLATIONS[ru_text]
        return f'data-ru="{ru_text}"{between}data-am="{new_am}"'
    else:
        # Keep as-is (shouldn't happen, but safe)
        return match.group(0)

# Apply replacements
pattern = r'data-ru="([^"]*)"([^>]*?)data-am="([^"]*)"'
new_content, count = re.subn(pattern, replace_am, content)

print(f"Replaced {count} data-am attributes")

