INSERT OR IGNORE INTO site_content (id, section_key, section_name, content_json, sort_order, updated_at) VALUES (137, 'faq', 'Частые вопросы (FAQ)', '[{"ru":"FAQ","am":"ՀՏՀ"},{"ru":"Частые вопросы","am":"Հաճախ տրվող հարցեր"},{"ru":"Могут ли заблокировать мой кабинет?","am":"Կարող են արգելափակել իմ կաբինետը։"},{"ru":"За всё время нашей работы ни один кабинет клиента не получил блокировку. Мы используем реальные аккаунты с историей покупок, собственный склад и естественное распределение по географии.","am":"Մեր աշխատանքի ողջ ընթացքում ոց մի հաճախորդի կաբինետ չի արգելափակվել: Մենք օգտագործում ենք իրական հաշիվներ գնումների պատմությամբ, սեփական պահեստ և բնական աշխարհագրական բաշխում:"},{"ru":"Как быстро начнётся продвижение?","am":"Ինչքան արագ կսկսվի առաջխաղացումը։"},{"ru":"В течение 24 часов после согласования стратегии и оплаты.","am":"24 ժամվա ընթացքում ստրատեգիայի համաձայնեցումից և վճարման հետո:"},{"ru":"Выкупы делают реальные люди или боты?","am":"Գնումները կատարում են իրական մարդիկ թե։ բոտեր։"},{"ru":"Только реальные люди. У нас собственный склад с устройствами и реальными аккаунтами. Каждый выкуп делается вручную, никаких ботов.","am":"Միայն իրական մարդիկ: Մենք ունենք սեփական պահեստ սարքերով և իրական հաշիվներով: Եվ գնումները կատարվում են ձեռքով, ոչ մի բոտ:"},{"ru":"Почему не все выкупы получают отзывы?","am":"Ինչու ոչ բոլոր գնումներն են ստանում կարծիքներ։"},{"ru":"Для безопасности вашего кабинета мы публикуем отзывы не более чем на 50% выкупленных товаров. Это имитирует естественное поведение покупателей.","am":"Ձեր կաբինետի անվտանգության համար կարծիքները հրապարակում ենք գնված ապրանքների ոչ ավելի քան 50%-ի համար: Սա նմանակում է գնորդների բնական վարքագիցը:"},{"ru":"Можно ли заказать только отзывы без выкупов?","am":"Հնարավոր է պատվիրել միայն կարծիքներ առանց գնումների։"},{"ru":"Да, мы можем выкупить товар для фото/видео отзыва и затем сделать возврат на ПВЗ. Стоимость уточняйте у менеджера.","am":"Այո, մենք կարող ենք գնել ապրանքը լուսանկար/տեսանյութ կարծիքի համար և հ