INSERT OR REPLACE INTO site_content (section_key, section_name, content_json, sort_order) VALUES
  ('about', 'О компании', '[{"ru": "О компании", "am": "Ընկերության մասին"}, {"ru": "Что такое", "am": "Ի՞նչ է Go to Top-ը"}, {"ru": "«Go to Top» — сервис развития Вашего бизнеса на маркетплейсах с помощью комплексного продвижения и услуги выкупов по ключевым словам. Для долгосрочного закрепления товара в TOPе.", "am": "«Go to Top» — ձեր բիզնեսի զարգացման ծառայություն մարկետփլեյսներում՝ ինքնագնումների միջոցով առաջխաղացման մեթոդ է TOP-ում երկարաժամկետ դիրքավորվելու համար:"}, {"ru": "Наша команда профессионалов с 2021 года работает на результат. У нас собственные склады и офисы в Ереване. Используем для выкупов Вашего товара только реальные аккаунты людей и производим всё вручную.", "am": "Մեր մասնագետների թիմը 2021 թվականից աշխատում է արդյունքի համար: Մենք ունենք սեփական պահեստներ և գրասենյակներ Երևանում: Գնումների համար օգտագործում ենք միայն իրական մարդկանց հաշիվներ և ամեն ինչ անում ենք ձեռքով:"}, {"ru": "Наилучший результат Вы получите, воспользовавшись комплексом наших услуг!", "am": "Լավագույն արդյունքը կստանաք օգտվելով մեր ծառայությունների փաթեթը!"}, {"ru": "Заказать сейчас", "am": "Պատվիրել հիմա"}]', 5);
INSERT OR REPLACE INTO site_content (section_key, section_name, content_json, sort_order) VALUES
  ('services', 'Наши услуги', '[{"ru": "Наши услуги", "am": "Մեր ծառայությունները"}, {"ru": "Полный спектр продвижения на WB", "am": "WB-ում առաջխաղացման լիարժեք սպեկտր"}, {"ru": "Выкупы живыми людьми, отзывы с реальными фото, профессиональные фотосессии — всё для вашего товара", "am": "Գնումներ իրական մարդկանցով, կարծիքներ իրական լուսանկարներով, մասնագիտական լուսանկարահանումներ — ամենը ձեր ապրանքի համար"}, {"ru": "Выкупы по ключевым запросам", "am": "Գնումներ բանալի հարցումներով"}, {"ru": "Ваш товар выкупается реальными людьми с реальных аккаунтов в разные ПВЗ по всему Еревану.", "am": "Ձեր ապրանքը գնվում է իրական մարդկանցով։ Իրական հաշիվներից տարբեր ՊՎԶ-ներով ամբողջ Երևանում:"}, {"ru": "Реальные аккаунты с историей покупок", "am": "Իրական հաշիվներ գնումների պատմությամբ"}, {"ru": "Географическое распределение", "am": "Աշխարհագրական բաշխում"}, {"ru": "Естественное поведение покупателей", "am": "Գնորդների բնական վարքագիծ"}, {"ru": "Забор товара из ПВЗ", "am": "Ապրանքի ստացում ՊՎԶ-ից"}, {"ru": "Отзывы и оценки", "am": "Կարծիքներ և գնահատականներ"}, {"ru": "Развёрнутые отзывы с фото и видео от реальных аккаунтов для повышения рейтинга.", "am": "Մանրամասն կարծիքներ լուսանկարներով և տեսանյութով իրական հաշիվներից վարկանիշի բարձրացման համար:"}, {"ru": "Текст отзыва + фото/видео", "am": "Կարծիքի տեքստ + լուսանկար/տեսանյութ"}, {"ru": "Профессиональная фотосессия", "am": "Մասնագիտական լուսանկարահանում"}, {"ru": "Разные локации и модели", "am": "Տարբեր վայրեր և մոդելներ"}, {"ru": "До 50% отзывов от выкупов", "am": "Մինչև 50% կարծիքներ գնումներից"}, {"ru": "Начать продвижение", "am": "Սկսել առաջխաղացումը"}, {"ru": "Фото и видеосъёмка", "am": "Լուսանկարահանում և տեսանկարահանում"}, {"ru": "Профессиональная съёмка товаров с моделями для карточек WB и отзывов.", "am": "Մասնագիտական ապրանքների լուսանկարահանում մոդելներով WB քարտերի և կարծիքների համար:"}, {"ru": "Женские и мужские модели", "am": "Կանացի և տղամարդկանցի մոդելներ"}, {"ru": "Предметная съёмка", "am": "Առարկայական լուսանկարահանում"}, {"ru": "Видеообзоры товаров", "am": "Ապրանքների տեսանյութներ"}, {"ru": "Ребёнок модель (до 14 лет)", "am": "Երեխա մոդել (մինչև 14 տարեկան)"}, {"ru": "Заказать съёмку", "am": "Պատվիրել լուսանկար"}]', 6);
