  ('calculator', 'Калькулятор (заголовки)', '[{"ru": "Калькулятор", "am": "Հաշվիչ"}, {"ru": "Рассчитайте стоимость услуг", "am": "Հաշվեք ծառայությունների արժեքը"}, {"ru": "Выберите нужные услуги, укажите количество и узнайте сумму. Заказ оформляется в Telegram.", "am": "Ընտրեք անհրաժեշտ ծառայությունները, նշեք քանակը և իմացեք գումարը: Պատվերը ձևակերպվում է Telegram-ով:"}, {"ru": "Выкупы", "am": "Գնումներ"}, {"ru": "Отзывы", "am": "Կարծիքներ"}, {"ru": "Фотосъёмка", "am": "Լուսանկարահանում"}, {"ru": "ФФ", "am": "Ֆուլֆիլմենթ"}, {"ru": "Логистика", "am": "Լոգիստիկա"}, {"ru": "Прочие услуги", "am": "Այլ ծառայություններ"}, {"ru": "Выкуп + забор из ПВЗ", "am": "Գնում + ստացում ՊՎԶ-ից"}, {"ru": "Чем больше выкупов — тем дешевле:", "am": "Որքան շատ գնումներ — այնքան էժան:"}, {"ru": "1-20 шт → ֏2 000 | 21-40 шт → ֏1 700 | 41-60 шт → ֏1 500 | 60+ шт → ֏1 250", "am": "1-20 հատ → ֏2 000 | 21-40 հատ → ֏1 700 | 41-60 հատ → ֏1 500 | 60+ հատ → ֏1 250"}, {"ru": "Выкуп КГТ + забор из ПВЗ", "am": "Ծանրաքաշ ապրանքի գնում + ստացում ՊՎԶ-ից"}, {"ru": "Оценка", "am": "Գնահատական"}, {"ru": "Оценка + отзыв", "am": "Գնահատական + կարծիք"}, {"ru": "Вопрос к товару", "am": "Հարց ապրանքի վերաբերյալ"}, {"ru": "Написание текста отзыва", "am": "Կարծիքի տեքստի գրում"}, {"ru": "Подписка на бренд / страницу", "am": "Բրենդի / էջի բաժանորդագրություն"}, {"ru": "Фотосессия в гардеробной WB (жен. модель)", "am": "Լուսանկարահանում WB հագուստապահարանում (կին մոդել)"}, {"ru": "Фотосессия в гардеробной WB (муж. модель)", "am": "Լուսանկարահանում WB հագուստապահարանում (տղամարդ մոդել)"}, {"ru": "Предметная фотосъёмка (3 фото)", "am": "Առարկայական լուսանկարահանում (3 լուսանկար)"}, {"ru": "Предметная съёмка (крупное / техника, 3 фото)", "am": "Առարկայական լուսանկարահանում (խոշոր / տեխնիկա, 3 լուս.)"}, {"ru": "Ребёнок модель (до 14 лет)", "am": "Երեխա մոդել (մինչև 14 տարեկան)"}, {"ru": "Видеообзор товара", "am": "Ապրանքի վիդեոհոլովակ"}, {"ru": "Замена штрихкода", "am": "Շտրիխկոդի փոխարինում"}, {"ru": "Переупаковка (наш